import styled from 'styled-components';

export const Bar = styled.ul`
  position: fixed;
  width: 100%;
  background: rgba(45,45,45,0.98);
  color: #fff;
  margin: 0;
  padding: 0;
  list-style-type: none;
`;

export const Logo = styled.li`
  margin-left: 30px;
  text-align: center;
  font-size: 30px;
  vertical-align: center;
  cursor: pointer;
  flex-grow: 6;
  float: left;
`;

export const Item = styled.li`
  margin-left: 20px;
  cursor: pointer;
  &:hover {
    opacity: 0.65;
  }
`;
