import React, { Component } from 'react';
import { Bar, Logo, MenuItem, Item, Icon } from './style';

class TopBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMenu: false,
    };
  }

  toggle() {
    this.setState({
      showMenu: !this.state.showMenu,
    });
    console.log('The link was clicked.', this.state.showMenu);
  }

  render() {
    console.log('this.props.showInMenu', this.props.showInMenu);
    return (
      <Bar>
        <Logo href="#home"> ShaSha Villa </Logo>
        <MenuItem showInMenu={this.props.showInMenu}>
          <Item href="#facilities"> Facilities </Item>
          <Item href="#tariff" > Tariff </Item>
          <Item href="#gallery"> Gallery </Item>
          <Item href="#map"> Location </Item>
          <Item href="#contact"> Contact </Item>
            <Icon href="javascript:void(0);" onClick={(e) => { this.toggle();}} >
            <i className="fa fa-bars"></i>
          </Icon>
        </MenuItem>
      </Bar>
    );
  }
}

export default TopBar;
