import styled from 'styled-components';

export const Bar = styled.div`
  height: 50px;
  background: rgba(45,45,45,0.98);
  color: #fff;
  display: flex-container;
  width: 100%;
  position: fixed;
  z-index: 9999;
`;


export const Logo = styled.a`
  padding-left: 30px;
  padding-top: 6px;
  font-size: 30px;
  cursor: pointer;
  float: left;
  text-decoration: none;
  color: #fff;
  @media (max-width: 600px) {
    padding-left: 10px;
    font-size: 18px;
  }
`;

export const MenuItem = styled.div`
  display: flex;
  justify-content: flex-end;
  color: #fff;
  padding-right: 10%;
  padding-top: 15px;
  @media (max-width: 600px) {
    display: block;
    background: rgba(45,45,45,0.98);
    padding-top: 30px;
    padding-bottom: 15px;
    padding-left: 10px;
  }

`;

export const Item = styled.a`
  padding-left: 20px;
  cursor: pointer;
  text-decoration: none;
  color: #fff;
  &:hover {
    opacity: 0.65;
  }
  @media (max-width: 600px) {
    padding-left: 5px;
  }
`;
