import styled, { keyframes } from 'styled-components';

export const moveUp = keyframes`
  from {
    margin-top: 200px;
  }

  to {
    margin-top: 50px;
  }
`;


export const BigText = styled.p`
  text-align: center;
  font-size: 20px;
  margin-top: 50px;
  animation: ${moveUp} 1s;
`;

export const LeftSide = styled.div`
  float: left;
  display: inline-block;
  width: 50%;
  border: 1px solid;
`;

export const RightSide = styled.div`
  float: right;
  display: inline-block;
  width: 50%;
`;
